    constructor(){
        super();
        this.state={
            international:{
                name : "International Express",
                psize : "300 cm",
                pweight : "20 kg",
                slogan : "We send packages 'internationally' anywhere in the world, 365 days a year"
            },
            domestic:{
                name : "Doemstic Express",
                psize : "500 cm",
                pweight : "30 kg",
                slogan : "We send packages 'domestically' anywhere in the India, 365 days a year"
            },
            cold:{
                name : "Cold Express",
                psize : "100 cm",
                pweight : "15 kg",
                slogan : "We send 'cold' packages anywhere in the India, 365 days a year"
            },
            mail:{
                name : "Mail Express",
                psize : "40 cm",
                pweight : "100 g",
                slogan : "We send 'mailers' and 'letters' anywhere in the India, 365 days a year"
            },
            services : ["international", "domestic", "cold", "mail"],
            name : false
            
        };
    }
    componentDidMount(){
        
    }
    render(){
        
        return(
            <div className="row m-5">
                {this.state.services.indexOf(this.props.match.params.servicename)==-1?<Redirect to="/" />:null}
                <div className="col-md-8 col-xs-12">
                    <h4 className ="mb-3" style={{color:"teal"}}>{}</h4>
                    <p>{}</p>
                    <button className="btn btn-primary" style={{width:"100%"}}>Book Service</button>
                </div>
                <div className="col-md-3 col-xs-12">
                    <input type="text"></input>
                </div>
            </div>
        );
    }
}

export default Service;
