import React from 'react';
import { Redirect } from 'react-router';

class Service extends React.Component{
    constructor(){
        super();
        this.state={
            service:{}
        };
        
    }
    render(){
                if (this.props.service){
                    return(<div className="row m-5">
                    <div className="col-md-8 col-xs-12">
                        <h4 className ="mb-3" style={{color:"teal"}}>{this.props.service.name}</h4>
                        <p>{this.props.service.slogan}</p>
                        <h6 className="mb-3 mt-4"><strong style={{borderBottom:"2px solid orange"}}>Parcel Size</strong></h6>
                        Package can size up to {this.props.service.psize} (total dimensions of 3 sides) and {this.props.service.pweight}.
                        <h6 className="mb-3 mt-4"><strong  style={{borderBottom:"2px solid orange"}}>Package Charges</strong></h6>
                        Packages are charged on the basis on their weightand travel distance.
                        <h6 className="mb-3 mt-4"><strong  style={{borderBottom:"2px solid orange"}}>FAQ</strong></h6>
                        <div className="mt-4 mb-3 list-group">
                            <p className="list-group-item list-group-item-action mb-0">When will InfyExpress deliver my parcel</p>
                            <p className="list-group-item list-group-item-action mb-0">What happen if something goes wrong?</p>
                            <p className="list-group-item list-group-item-action mb-0">Can I send envelopes?</p>
                            <p className="list-group-item list-group-item-action mb-0">What should I do if my parcel is delayed or lost?</p>
                            <p className="list-group-item list-group-item-action">When will my parcel be delivered></p>
                        </div>
                        <button className="btn btn-primary" style={{width:"100%"}}>Book Service</button>
                    </div>
                    <div className="col-md-3 col-xs-12">
                    <div className="row">
                        <div className="col-12">

                        </div>
                        <div className="col-12">
                        <div class="card p-0" style={{width:"18em"}}>
                            <div class="card-body">
                                <h5 class="card-title">Card title</h5>
                                <h6 class="card-subtitle mb-2 text-muted">Card subtitle</h6>
                                <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                <a href="#" class="card-link">Card link</a>
                                <a href="#" class="card-link">Another link</a>
                            </div>
                        </div>
                        </div>
                    </div>
                    </div>
                </div>);
                }
                return null;
        
        
        
    }
}

export default Service;
