import { ADD_USER_FAILED, ADD_USER_RECEIVED, ADD_USER_REQUEST, FETCH_SERVICE_FAILED, FETCH_SERVICE_RECEIVED, FETCH_SERVICE_REQUEST, LOGIN_USER_FAILED, LOGIN_USER_RECEIVED, LOGIN_USER_REQUEST, LOGOUT_USER_RECEIVED, SUBMIT_QUOTE_FAILED, SUBMIT_QUOTE_RECIEVED, SUBMIT_QUOTE_REQUEST } from "./ActionType"

export const loginUserRequest = ()=>{
    return{
        type : LOGIN_USER_REQUEST
    }
};

export const loginUserReceived = (details)=>{
    return {
        type : LOGIN_USER_RECEIVED,
        details
    }
};

export const loginUserFailed = (err)=>{
    return{
        type : LOGIN_USER_FAILED,
        err
    }
};

export const loginUser = (creds)=>(dispatch)=>{
    dispatch(loginUserRequest());
    fetch('http://localhost:3000/users/login',{
        method : 'POST',
        headers : {
            'Content-Type' : "application/json"
        },
        body : JSON.stringify(creds)
    })
    .then((response)=>{
        if (response.ok){
            return response;
        }
        else{
            var err= new Error(response.status);
            err.response = response;
            throw err;
        }
    })
    .then((response)=>response.json())
    .then((response)=>{
        if(response.success){
            localStorage.setItem('token',response.token);
            localStorage.setItem('creds',response.details);
            console.log(response);
            dispatch(loginUserReceived({token:response.token}));
        }
        else{
            dispatch(loginUserFailed({err:err}))
        }
    })
}

export const addUserRequest = ()=>{
    return{
        type : ADD_USER_REQUEST
    }
};

export const addUserReceived = (details)=>{
    return {
        type : ADD_USER_RECEIVED,
        details
    }
};

export const addUserFailed = (err)=>{
    return{
        type : ADD_USER_FAILED,
        err
    }
};

export const addUser = (creds)=>(dispatch)=>{
    dispatch(addUserRequest());
    fetch('http://localhost:3000/users/signup',{
        method : 'POST',
        headers : {
            'Content-Type' : "application/json"
        },
        body : JSON.stringify(creds)
    })
    .then((response)=>{
        if (response.ok){
            return response;
        }
        else{
            var err= new Error('Error :'+response.status+': '+response.statusText);
            err.response = response;
            throw err;
        }
    },(err)=>{throw err})
    .then((response)=>response.json())
    .then((response)=>{
        if(response.success){
            localStorage.setItem('token',response.token);
            localStorage.setItem('creds',response.creds);
            dispatch(addUserReceived());
            dispatch(loginUser({username:creds.username,password:creds.password}));
        }
        else{
            dispatch(addUserFailed({err:err}))
        }
    })
    .catch((err)=>dispatch(addUserFailed(err.message)));
}

export const logoutUserRequest = ()=>{
    return{
        type : LOGOUT_USER_REQUEST
    }
};

export const logoutUserReceived = ()=>{
    return{
        type : LOGOUT_USER_RECEIVED
    }
};

export const logoutUser = ()=>(dispatch)=>{
    dispatch(logoutUserRequest())
    fetch('http://localhost:3000/users/logout',{
        method:'POST',
        headers:{
            'Content-Type':"application/json"
        }
    },(err)=>{throw err})
    .then((response)=>response.json())
    .then(response=>{
        dispatch(logoutUserReceived());
    })
};

export const fetchServiceRequest = () =>{
    return{
        type : FETCH_SERVICE_REQUEST
    }
}

export const fetchServiceReceived = (services) =>{
    return{
        type : FETCH_SERVICE_RECEIVED,
        services: services
    }
}

export const fetchServiceFailed = (err) =>{
    return{
        type : FETCH_SERVICE_FAILED,
        err : err
    }
}

export const fetchService = ()=> (dispatch) =>{
    dispatch(fetchServiceRequest());
    fetch('https://localhost:3000/service',{
        method : 'GET',
        headers : {
            'Content-Type' : "application/json"
        }
    })
    .then((response)=>response.json())
    .then(response=>{
        console.log(response);
        if (response.ok){
            dispatch(fetchServiceReceived(response.services));
        }
        else{
            dispatch(fetchServiceFailed({err:"err"}))
        }
    })
    .catch((err)=>dispatch(fetchServiceFailed(err)));
}

export const submitQuoteRequest = ()=>{
    return{
        type : SUBMIT_QUOTE_REQUEST
    }
}

export const submitQuoteReceived = (data)=>{
    return{
        type : SUBMIT_QUOTE_RECIEVED
    }
}

export const submitQuoteFailed = (err)=>{
    return{
        type : SUBMIT_QUOTE_FAILED,
        err: err
    }
}

export const submitQuote = (data)=>(dispatch)=>{
    dispatch(submitQuoteRequest());
    dispatch(submitQuoteReceived());
}
