import React from 'react';
import {Switch, Route, Redirect, withRouter} from 'react-router-dom';
import Home from './Home.js';
import { connect } from 'react-redux';
import Login from './Login.js';
import Register from './Register.js';
import Header from './Header.js';
import Footer from './Footer.js';
import ServicesList from './ServiceList.js';
import Service from './Service';
import { fetchService } from '../redux/ActionCreators.js';

var mapStateToProps = function(state){
    return {
      service : state.Service
    };
  }
var mapDispatchToProps = function(dispatch){
    return {
        fetchService : ()=>dispatch(fetchService())
    }
  }

class MainComponent extends React.Component{
    constructor(){
        super();
        this.state={}
    }
    componentDidMount(){
        this.props.fetchService();
    }

    render(){
        const ServiceWithId = ({match}) => {

            return(
            <Service service={this.props.services.filter((service)=>service._id==this.props.id)[0]} />
            )
        };
        return(
            <>
                <Header />
                <Switch>
                    <Route  exact path="/" 
                            component={Home} />
                    <Route  exact path="/login" 
                            component={Login} />
                    <Route  exact path="/register" 
                            component={Register}/>
                    <Route  exact path="/listofservices" 
                            component={()=><ServicesList
                                            service = {this.props.service} 
                                            />}/>
                    <Route exact path ="/listofservices/:serviceId" 
                            component={ServiceWithId}/>
                    <Redirect to="/" />
                </Switch>
                <Footer />
            </>
        );
    }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(MainComponent));
