import React from 'react';
import person from '../images/person.svg';
import lock from '../images/lock.svg';

class Login extends React.Component{

    constructor(){
        super();
        this.state={
            username : "",
            password : "",
            error: {
                username : false,
                password : false
            }
        }
    }

    handleChange=(e)=>{
        console.log(this.state);
        this.setState({[e.target.name]:e.target.value});
    }

    handleSubmit = (e) =>{
        e.preventDefault();
        let username = false, password = false;
        let error = this.state.error;
        console.log(this.state.username.length);
        if (!(this.state.username.length<31 && this.state.username.length>2)){
            this.setState({error:{error,username:"Length of username should be between 2 and 31"}})
        }
        else{
            this.setState({error:{error,username:false}})
        }
        if (!(this.state.password.length<31 && this.state.password.length>8)){
            this.setState({error:{error,password:"Length of username should be between 7 and 31"}})
        }
        else{
            this.setState({error:{error,password:false}})
        }
    }
    render(){
        return(<>
            <div className="container">
            <div className="card m-5" style={{borderRadius:"20px"}}>
                <div className="card-body">
                    <div className="row">
                        <div className="col-12 col-sm-12 col-md-6">
                            
                        </div>
                        <div className="col-10 offset-1 col-sm-10 offset-sm-2 col-md-6 offset-md-0">
                            <h2 className="text-center mb-5 mt-5"><span  style={{borderBottom:"2px solid orange"}}>LOGIN</span></h2>
                        <form onSubmit={this.handleSubmit}>
                            <div class="form-group">
                                <label for="username">Username</label>
                                <div class="input-group">
                                    <div class="input-group-prepend">
                                    <span class="input-group-text" id="usernameprepend"><img src={person}/></span>
                                    </div>
                                    <input name="username" type="text" class={this.state.error.username ? "form-control is-invalid" : "form-control"} value ={this.state.username} onChange={this.handleChange} id="username" placeholder="Username" aria-describedby="usernameprepend" required />
                                    <div class="invalid-feedback">
                                    {this.state.error.username}
                                    </div>
                                </div>
                            </div>
                            <div class="form-group ">
                                <label for="password">Password</label>
                                <div class="input-group">
                                    <div class="input-group-prepend">
                                    <span class="input-group-text" id="passwordprepend"><img src={lock}/></span>
                                    </div>
                                    <input name="password" type="password" class={this.state.error.password ? "form-control is-invalid" : "form-control"} value ={this.state.password} onChange={this.handleChange} id="password" placeholder="Password" aria-describedby="passwordprepend" required />
                                    <div class="invalid-feedback">
                                    {this.state.error.password}
                                    </div>
                                </div>
                            </div>
                            <center>
                                <button type="submit" class="btn btn-primary mt-3 mb-2">Submit</button>
                                <p>Don't have an account? <a href="/register" style={{textDecoration:"None"}}>Create new</a></p>
                            </center>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
            </div>
        </>);
    }
}

export default Login;
