import React from 'react';
import {Switch, Route, Redirect, withRouter} from 'react-router-dom';
import Home from './Home.js';
import Login from './Login.js';
import Register from './Register.js';
import Header from './Header.js';
import Footer from './Footer.js';
import ServicesList from './ServiceList.js';
import Service from './Service';

var mapStateToProps = function(state){
    return {
      auth: state.Auth,
      rides : state.FetchRide
    };
  }
var mapDispatchToProps = function(dispatch){
    return {
        loginUser : (username,password) => dispatch(loginUser(username,password)),
        logoutUser : () => dispatch(logoutUser()),
        bookRide : (rideId)=> dispatch(bookRide(rideId)),
        cancelRide : (rideId) => dispatch(cancelRide(rideId)),
        offerRide : (rideDetails) => dispatch(offerRide(rideDetails)),
        registerUser : (data) => dispatch(registerUser(data)),
        fetchRide : ()=> dispatch(fetchRide())
    }
  }

class MainComponent extends React.Component{
    constructor(){
        super();
        this.state={}
    }

    render(){
        return(
            <>
                <Header />
                <Switch>
                    <Route exact path="/" component={Home} />
                    <Route exact path="/login" component={Login} />
                    <Route exact path="/register" component={Register}/>
                    <Route exact path="/listofservices" component={ServicesList}/>
                    <Route exact path ="/listofservices/:servicename" component={Service}/>
                    <Redirect to="/" />
                </Switch>
                <Footer />
            </>
        );
    }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(MainComponent));
