import React from 'react';
import { Redirect } from 'react-router';

class Service extends React.Component{
    constructor(){
        super();
        this.state={
            service:{}
        };
        
    }
    render(){
                if (this.props.service){
                    return(<div className="row m-5">
                    <div className="col-md-8 col-xs-12">
                        <h4 className ="mb-3" style={{color:"teal"}}>{this.props.service.name}</h4>
                        <p>{this.props.service.slogan}</p>
                        <h6 className="mb-3 mt-4"><strong style={{borderBottom:"2px solid orange"}}>Parcel Size</strong></h6>
                        Package can size up to {this.props.service.psize} (total dimensions of 3 sides) and {this.props.service.pweight}.
                        <h6 className="mb-3 mt-4"><strong  style={{borderBottom:"2px solid orange"}}>Package Charges</strong></h6>
                        Packages are charged on the basis on their weightand travel distance.
                        <h6 className="mb-3 mt-4"><strong  style={{borderBottom:"2px solid orange"}}>FAQ</strong></h6>
                        <div class="list-group">
                            <p class="list-group-item list-group-item-action mb-0">When will InfyExpress deliver my parcel</p>
                            <p class="list-group-item list-group-item-action mb-0">What happen if something goes wrong?</p>
                            <p class="list-group-item list-group-item-action mb-0">Can I send envelopes?</p>
                            <p class="list-group-item list-group-item-action mt-0">What should I do if my parcel is delayed or lost?</p>
                            <p class="list-group-item list-group-item-action mt-0">When will my parcel be delivered></p>
                        </div>
                        <button className="btn btn-primary" style={{width:"100%"}}>Book Service</button>
                    </div>
                    <div className="col-md-3 col-xs-12">
                    <input type="text"></input>
                    </div>
                </div>);
                }
                return null;
        
        
        
    }
}

export default Service;
