import { FETCH_SERVICE_FAILED, FETCH_SERVICE_RECEIVED, FETCH_SERVICE_REQUEST, SUBMIT_GETQUOTE_FAILED, SUBMIT_GETQUOTE_RECIEVED, SUBMIT_GETQUOTE_REQUEST } from "./ActionType"

export const fetchServiceRequest = () =>{
    return{
        type : FETCH_SERVICE_REQUEST
    }
}

export const fetchServiceReceived = (services) =>{
    return{
        type : FETCH_SERVICE_RECEIVED,
        services: services
    }
}

export const fetchServiceFailed = (err) =>{
    return{
        type : FETCH_SERVICE_FAILED,
        err : err
    }
}

export const fetchService = ()=> (dispatch) =>{
    dispatch(fetchServiceRequest());
    dispatch(fetchServiceReceived([{
        _id: "1",
        name : "International Express",
        psize : "300 cm",
        pweight : "20 kg",
        slogan : "We send packages 'internationally' anywhere in the world, 365 days a year"
    },
    {
        _id: "2",
        name : "Doemstic Express",
        psize : "500 cm",
        pweight : "30 kg",
        slogan : "We send packages 'domestically' anywhere in the India, 365 days a year"
    },
    {
        _id: "3",
        name : "Cold Express",
        psize : "100 cm",
        pweight : "15 kg",
        slogan : "We send 'cold' packages anywhere in the India, 365 days a year"
    },
    {
        _id: "4",
        name : "Mail Express",
        psize : "40 cm",
        pweight : "100 g",
        slogan : "We send 'mailers' and 'letters' anywhere in the India, 365 days a year"
    }]));
}

export const submitGetQuoteRequest = ()=>{
    return{
        type : SUBMIT_GETQUOTE_REQUEST
    }
}

export const submitGetQuoteReceived = (data)=>{
    return{
        type : SUBMIT_GETQUOTE_RECIEVED
    }
}

export const submitGetQuoteFailed = (err)=>{
    return{
        type : SUBMIT_GETQUOTE_FAILED,
        err: err
    }
}

export const submitGetQuote = (data)=>(dispatch)=>{
    dispatch(submitGetQuoteRequest());
    dispatch(submitGetQuoteReceived());
}
