import React from 'react';

import {Link} from 'react-router-dom';
class ServicesList extends React.Component{
    constructor(){
        super();
        this.state={
            services : [
                {
                    international:{
                    name : "International Express",
                    psize : "300 cm",
                    pweight : "20 kg",
                    slogan : "We send packages 'internationally' anywhere in the world, 365 days a year"
                    }
                },
                {
                    domestic:{
                    name : "Doemstic Express",
                    psize : "500 cm",
                    pweight : "30 kg",
                    slogan : "We send packages 'domestically' anywhere in the India, 365 days a year"
                    }
                },
                {
                    cold:{
                    name : "Cold Express",
                    psize : "100 cm",
                    pweight : "15 kg",
                    slogan : "We send 'cold' packages anywhere in the India, 365 days a year"
                    }
                },
                {
                    mail:{
                    name : "Mail Express",
                    psize : "40 cm",
                    pweight : "100 g",
                    slogan : "We send 'mailers' and 'letters' anywhere in the India, 365 days a year"
                    }
                }
            ]
        };
    }

    render(){
        return( <>
                    <div className="bg-dark p-5">
                        <h3 className="bottom-left text-white">List of Services</h3>
                    </div>
                    <div className="container">
                        <div className="m-5">
                            <div className="row mb-4">
                                <div className="col-sm-8 offset-sm-2 text-center">
                                    <h3><span style={{borderBottom:"2px solid brown", color:"teal"}} >Infy Express</span></h3>
                                    <p>Infy Express is believed in seving the clients at their door step. Out thinking is "The best
                                        certification of a quality is the measurement of the scale of the smile on the customer face
                                    </p>
                                </div>
                            </div>
                            <div className="row">
                                {this.state.service}
                                <div className="col-lg-3 col-sm-6 mt-3">
                                    <div className="card home-card pr-0 pl-0 text-center" style={{borderRadius:"20px", backgroundColor:"skyblue"}}>
                                        <div className="card-body pb-0">
                                            <Link to={{pathname:"/listofservices/international" ,state:this.state.international}} style={{color:"black",textDecoration:"None"}}><p className="card-title"><strong>International Express</strong></p></Link>
                                            <p className="card-text">{this.state.international.slogan}</p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </>
        );
    }
}

export default ServicesList;
