import React from 'react';
import { Redirect } from 'react-router';

class Service extends React.Component{
    constructor(){
        super();
        this.state={
            service:{}
        };
        if (this.props){
            this.setState({service:this.props.services.filter((service)=>service._id==this.props._id)[0]});
            console.log(this.props.services);
        }
        
    }
    render(){
        if (this.props.service.isLoading || this.props.service.service || this.props.service.err){
            return (
                <div className="row m-5">
                    <div className="col-md-8 col-xs-12">
                        <h4 className ="mb-3" style={{color:"teal"}}></h4>
                            <p></p>
                        <button className="btn btn-primary" style={{width:"100%"}}>Book Service</button>
                    </div>
                    <div className="col-md-3 col-xs-12">
                    <input type="text"></input>
                    </div>
            </div>
            );
        }
        
    }
}

export default Service;
